	public static String get(final String url, final Map<String, String> param) {
		try {
			SSLUtil.setSSL();
			String body = Jsoup.connect(getUrl(url, param)).timeout(10000)
			.header("referer", REFERER_URL)
			.header("logic", LOGIC)
			.header("sbth", SBTH)
			.header("accept", ACCEPT)
			.header("accept-encoding", ACCEPT_ENCODING)
			.header("accept-language", ACCEPT_LANGUAGE)
			.userAgent(USER_AGENT).ignoreContentType(true).execute().body();
			return body;
		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			//sleep(); //비정상적인 요청 감지로 중간중간 쉬는 시간을 가짐
		}
		return null;
	}
	
